mapper.select:
   		select n.n_id,n.n_stuId,n.n_stuName,n.n_followTime,n.n_nextFollowTime,n.n_context,e.e_id,e.e_loginName,s.s_id,s.s_name,s_isReturnVisit
		from 
		netfollow n INNER JOIN employee e ON n.e_id=e.e_id 
		INNER JOIN student s on n.n_stuId = s.s_id


				and n.n_stuName like "%"#{n_stuName}"%"


				and e.e_id=#{e_id}



				and n.startn_followTime > =#{startn_followTime}



				and n.endn_followTime < =#{endn_followTime}


				and s.s_isReturnVisit=#{s_isReturnVisit}


				and n.n_followType=#{n_followType}


		limit #{page},#{pageSize}

    select count(1)
		from 
		netfollow n INNER JOIN employee e ON n.e_id=e.e_id 
		INNER JOIN student s on n.n_stuId = s.s_id


				and n.n_stuName like "%"#{n_stuName}"%"


				and e.e_id=#{e_id}



				and n.startn_followTime > =#{startn_followTime}



				and n.endn_followTime < =#{endn_followTime}


				and s.s_isReturnVisit=#{s_isReturnVisit}


				and n.n_followType=#{n_followType}
